&amp;nbsp;&amp;nbsp;&amp;lt;groupId&amp;gt;org.apache.flink&amp;lt;/groupId&amp;gt;
&amp;nbsp;&amp;nbsp;&amp;lt;artifactId&amp;gt;flink-java&amp;lt;/artifactId&amp;gt;
&amp;nbsp;&amp;nbsp;&amp;lt;version&amp;gt;1.6.2&amp;lt;/version&amp;gt;
&amp;lt;/dependency&amp;gt;
