import&amp;nbsp;java.lang.invoke.LambdaForm;
import&amp;nbsp;java.util.function.Consumer;

final&amp;nbsp;class&amp;nbsp;Test$$Lambda$1
implements&amp;nbsp;Consumer&amp;nbsp;{
